// src/components/NetworkPoints.js
import React, { useRef } from 'react';
import { useFrame } from '@react-three/fiber';
import * as THREE from 'three';

const NetworkPoints = () => {
  const pointsRef = useRef();

  // Generate random points
  const points = new Array(500).fill().map(() => ({
    position: [
      (Math.random() - 0.5) * 10,
      (Math.random() - 0.5) * 10,
      (Math.random() - 0.5) * 10,
    ],
  }));

  useFrame(() => {
    pointsRef.current.rotation.y += 0.001;
  });

  return (
    <points ref={pointsRef}>
      <bufferGeometry>
        <bufferAttribute
          attachObject={['attributes', 'position']}
          count={points.length}
          array={new Float32Array(points.flatMap(point => point.position))}
          itemSize={3}
        />
      </bufferGeometry>
      <pointsMaterial color="#00bfff" size={0.1} />
    </points>
  );
};

export default NetworkPoints;
