// src/NetworkAnimation.js
import React, { useRef, useEffect, useMemo } from 'react';
import { Canvas, useFrame } from '@react-three/fiber';
import { OrbitControls } from '@react-three/drei';
import * as THREE from 'three';

// Composant Particles pour gérer les particules et leur animation
const Particles = () => {
  const particlesRef = useRef(); // Référence pour accéder à l'objet points

  // Utilisation de useMemo pour créer la géométrie des particules une seule fois
  const particlesGeometry = useMemo(() => {
    const particleCount = 500; // Nombre de particules
    const positions = new Float32Array(pparticleCount * 3); // Tableau pour stocker les positions des particules

    // Initialiser les positions des particules de manière aléatoire
    for (let i = 0; i < particleCount; i++) {
      positions[i * 3] = (Math.random() - 0.5) * 10;
      positions[i * 3 + 1] = (Math.random() - 0.5) * 10;
      positions[i * 3 + 2] = (Math.random() - 0.5) * 10;
    }

    // Créer la géométrie des particules et définir les positions
    const geometry = new THREE.BufferGeometry();
    geometry.setAttribute('position', new THREE.BufferAttribute(positions, 3));

    return geometry; // Retourner la géométrie pour l'utiliser dans le rendu
  }, []); // Le tableau vide comme deuxième argument signifie que cette logique s'exécute une seule fois

  // Utilisation de useFrame pour animer les particules à chaque frame
  useFrame(() => {
    if (particlesRef.current) { // Vérifier si particlesRef est défini
      particlesRef.current.rotation.x += 0.001; // Rotation sur l'axe x
      particlesRef.current.rotation.y += 0.001; // Rotation sur l'axe y
    }
  });

  return (
    // Composant points pour rendre les particules, avec la géométrie créée et le matériau
    <points ref={particlesRef} geometry={particlesGeometry}>
      <pointsMaterial color="#00ff00" size={0.1} /> {/* Matériau des particules */}
    </points>
  );
};

// Composant principal pour encapsuler la scène Canvas de react-three-fiber
const NetworkAnimation = () => {
  return (
    // Canvas de react-three-fiber pour rendre la scène 3D
    <Canvas camera={{ position: [0, 0, 10], fov: 75 }}>
      <ambientLight intensity={0.5} /> {/* Lumière ambiante */}
      <pointLight position={[10, 10, 10]} /> {/* Lumière ponctuelle */}
      <Particles /> {/* Ajout du composant Particles */}
      <OrbitControls enableZoom={false} /> {/* Contrôles de la caméra pour permettre l'interaction, zoom désactivé */}
    </Canvas>
  );
};

export default NetworkAnimation;
